mapper.update:
UPDATE exam SET 1=1

    , name = #{name}


    , lesson_id = #{lesson_id}


    , releaseTime = #{releaseTime}


    , duration = #{duration}

WHERE id = #{id}

mapper.select:
SELECT a.id, a.name, a.releaseTime AS time, a.duration, b.name AS lesson_name, c.name AS teacher_name, d.name AS department_name, f.name AS major_name, FROM exam a LEFT JOIN lesson b ON a.lesson_id = b.id LEFT JOIN user c ON b.teacher_id = c.id LEFT JOIN department d ON b.department_id = d.id LEFT JOIN major f ON b.major_id = f.id WHERE 1=1
AND b.department_id = #{departmentId}
AND b.major_id = #{majorId}
AND a.lesson_id = #{lessonId}
AND b.teacher_id = #{teacherId}
AND a.name LIKE #{name}
ORDER BY a.updated_time DESC
